declare module 'network' {
    type Interface = {
        name: string
        ip_address: string
        netmask: string
    }

    const module: {
        get_active_interface: (callback: (error: Error | null, interface: Interface) => void) => void
    };

    export default module;
}

declare module 'network-calculator' {
    const module: (ip: string, netmask: string) => ({
        network: string
        bitmask: number
        firsthost: string
        broadcast: string
        lasthost: string
        totalhost: number
    });
    export default module;
}

declare module 'node-arp' {
    const module: {
        getMAC: (ip: string, callback: (error: null | Error, mac: string) => void) => void;
    };
    export default module;
}

declare module 'get-ip-range' {
    const getIpRange: (ip: string) => string[];
    export default getIpRange;
}